import React from 'react';
import MyContext from './MyContext';

class Filho extends React.Component {
  render() {
    return (
      <MyContext.Consumer>
        {
          (money, spend) => (
            <div>
              <span>
                O filho possui heran√ßa de ${money}.
              </span>
              <button type="button" onClick={spend}>Gastar R$</button>
            </div>
          )
        }
      </MyContext.Consumer>
    );
  }
}

export default Filho;
