import React from 'react';
import MyContext from './MyContext';
import Pai from './Pai';

class Avo extends React.Component {
  constructor(props) {
    super(props);
    this.state = { money: 10000}
    this.spend = this.spend.bind(this);
  }
spend() {
  this.setState((prevState) => ({money: prevState.money - 100}))
}

  render() {
    const contextValue = {
      money: this.state.money,
      spend: this.state.spend,
    }
    return (
      <div>
        <h2>Mensagem do avô:</h2>
        <h1>Aaaaaperte o botão para gastar R$ 100.</h1>
        <MyContext.Provider value={contextValue}>
          <Pai />
        </MyContext.Provider>
      </div>
    ); 
  }
}

export default Avo;
